mapper.select:
select count(*)
from bucketlike
where userid = #{userId}
and bkNo = #{bkNo}

mapper.update:
update bucketlist
set bklike = bklike - 1
where bkno = #{bkNo}

mapper.delete:
delete from bucketlike
where userid = #{userId}
and bkNo = #{bkNo}

mapper.update:
update bucketlist
set bklike = bklike + 1
where bkno = #{bkNo}

mapper.select:
select bklike
from bucketlist
where bkNo = #{bkNo}

select count(*)
from wishlist
where userid = #{userId}
and bkNo = #{bkNo}

mapper.delete:
delete from wishlist
where userid = #{userId}
and bkNo = #{bkNo}

mapper.select:
select *
from wishlist
where userid = #{userId}

select *
from sharebucket
