mapper.update:
update boardcomment 
set cmcontent = #{ cmContent }
where cmno = #{ cmNo }

update reply 
set RPCONTENT = #{ rpContent }
where RPNO = #{ rpNo }

update boardcomment 
set status = 'N'
where cmno = #{ cmNo }

update reply 
set RPSTATUS = 'N'
where RPNO = #{ rpNo }

mapper.delete:
delete 
from follow
where following=#{following} and follower=#{follower}

mapper.select:
select count(*)
from notify
where bno = #{ bno } and sinuser = #{ sinuser }

select count(*)
from notify
where cmno = #{ cmno } and sinuser = #{ sinuser }

select count(*)
from notify
where rpno = #{ rpno } and sinuser = #{ sinuser }

mapper.update:
update board
set blike = blike + 1
where bno = #{ bNo }

update board
set blike = blike - 1
where bno = #{ bNo }

mapper.select:
select count(*) 
from blike
where bno = #{ bNo } and userid = #{ userId }

select *
from board
where bno = #{ bNo }

mapper.update:
update board
set btitle = #{ bTitle }, bcontent = #{ bContent }
where bno = #{ bNo }

update board
set STATUS = 'N'
where bno = #{ bNo }

update bucketlist
set BKNAME = #{ bkName }, BKCONTENT = #{ bkContent }, TAG = #{ tag }
where bkno = #{ bkNo }

update media
set mweb = #{ mweb }
where bkno = #{ bkno }

update member
set PR_IMAGE = #{ prImage }
where userid = #{ userId }

mapper.select:
select count(*)
from SHAREBUCKET
where userid = #{ userid } and bkno = #{ bkNo }

mapper.update:
update SHAREBUCKET
set ENROLLDATE = SYSDATE
where userid = #{ userid } and bkno = #{ bkNo }

update bucketList
set ENROLLDATE = SYSDATE
where bkno = #{ bkNo }
